import axios from 'axios';
export default {
  data() {
    return {
      orderList: [],
      tableHeaders: [{
        title: 'ID',
        key: 'id',
        align: 'start'
      }, {
        title: '회원 EMAIL',
        key: 'memberEmail',
        align: 'start'
      }, {
        title: '주문상태',
        key: 'orderStatus',
        align: 'start'
      }, {
        title: 'ACTION',
        key: 'actions',
        align: 'start'
      }]
    };
  },
  async created() {
    try {
      const response = await axios.get(`${process.env.VUE_APP_API_BASE_URL}/order/list`);
      this.orderList = response.data.result;
    } catch (e) {
      console.log(e);
    }
  },
  methods: {
    async cancelOrder(id) {
      try {
        await axios.patch(`${process.env.VUE_APP_API_BASE_URL}/order/${id}/cancel`);
        window.location.reload();
      } catch (e) {
        console.log(e);
      }
    }
  }
};
